mapper.select:
SELECT `id` , `userId` , `payment` , `paymentType` , `company` , `monthlyInstallment` , `hasCanceled` , `buyerName` , `buyerPhoneNumber` , `statusDelivery` , `receiverName` , `receiverPhoneNumber` , `address` , `createdAt` , `updatedAt` FROM `invoice`
AND `id` = #{id}
AND `userId` = #{userId}
AND `payment` = #{payment}
AND `payment` like LOWER(concat('%',#{paymentType},'%'))
AND `company` like LOWER(concat('%',#{company},'%'))
AND `monthlyInstallment` like LOWER(concat('%',#{monthlyInstallment},'%'))
AND `hasCanceled` = #{hasCanceled}
AND `buyerName` like LOWER(concat('%',#{buyerName},'%'))
AND `buyerPhoneNumber` like concat('%',#{buyerPhoneNumber},'%')
AND `statusDelivery` = #{statusDelivery}
AND `receiverName` like LOWER(concat('%',#{receiverName},'%'))
AND `receiverPhoneNumber` like concat('%',#{receiverPhoneNumber},'%')
AND `address` like LOWER(concat('%',#{address},'%'))
AND `createdAt` = #{createdAt}
AND `updatedAt` = #{updatedAt}
ORDER BY `ID` DESC LIMIT 30
