mapper.select:
SELECT `id` , `userId` , `payment` , `hasCanceled` , `buyerName` , `buyerPhoneNumber` , `statusDelivery` , `receiverName` , `receiverPhoneNumber` , `address` , `createdAt` , `updatedAt` FROM `invoice`
AND `id` = #{id}
AND `userId` = #{userId}
AND `payment` = #{payment}
AND `hasCanceled` = #{hasCanceled}
AND `buyerName` like LOWER(concat('%',#{buyerName},'%'))
AND `buyerPhoneNumber` like concat('%',#{buyerPhoneNumber},'%')
AND `statusDelivery` = #{statusDelivery}
AND `receiverName` like LOWER(concat('%',#{receiverName},'%'))
AND `receiverPhoneNumber` like concat('%',#{receiverPhoneNumber},'%')
AND `address` like LOWER(concat('%',#{address},'%'))
AND `createdAt` BETWEEN #{createdAtSince} and #{createdAtUntil}
AND `updatedAt` BETWEEN #{updatedAtSince} and #{updatedAtUntil}
AND `id` < #{index}
ORDER BY `id` DESC
AND `id` > #{index}
ORDER BY `id` DESC
LIMIT #{limit};

SELECT `id` , `userId` , `payment` , `hasCanceled` , `buyerName` , `buyerPhoneNumber` , `statusDelivery` , `receiverName` , `receiverPhoneNumber` , `address` , `createdAt` , `updatedAt` FROM `invoice`
AND `id` = #{id}
AND `userId` = #{userId}
AND `payment` = #{payment}
AND `hasCanceled` = #{hasCanceled}
AND `buyerName` like LOWER(concat('%',#{buyerName},'%'))
AND `buyerPhoneNumber` like concat('%',#{buyerPhoneNumber},'%')
AND `statusDelivery` = #{statusDelivery}
AND `receiverName` like LOWER(concat('%',#{receiverName},'%'))
AND `receiverPhoneNumber` like concat('%',#{receiverPhoneNumber},'%')
AND `address` like LOWER(concat('%',#{address},'%'))
AND `createdAt` = #{createdAt}
AND `updatedAt` = #{updatedAt}
ORDER BY `ID` DESC LIMIT 30

